mapper.select:
SELECT COUNT(*) FROM API_INFO
AND NAME LIKE '%' || #{filter.name} || '%'
AND HTTP_METHOD = #{filter.httpMethod}

SELECT ID, NAME, HTTP_METHOD, URL
FROM API_INFO
WHERE ID = #{id}

mapper.update:
UPDATE API_INFO
SET NAME        = #{name},
    HTTP_METHOD = #{httpMethod},
    URL         = #{url}
WHERE ID = #{id}
