mapper.select:
SELECT COUNT(*)
FROM BOARD_MASTER A
LEFT OUTER JOIN USERS U
  ON A.AUTHOR_ID = U.ID


    AND A.NAME LIKE '%' || #{filter.name} || '%'


    AND U.AUTHOR LIKE '%' || #{filter.author} || '%'


    AND A.MODIFIED_TIME >= #{filter.startModifiedTime}


    AND A.MODIFIED_TIME <= #{filter.endModifiedTime}


    AND A.USE_YN = #{filter.useYn, typeHandler=BooleanToYnTypeHandler}


    AND A.REPLY_YN = #{filter.replyYn, typeHandler=BooleanToYnTypeHandler}


    AND A.FILE_ATTACH_YN = #{filter.fileAttachYn, typeHandler=BooleanToYnTypeHandler}

SELECT A.ID, A.NAME, A.INSTRUCTION, A.FILE_ATTACH_YN, A.REPLY_YN, A.USE_YN, A.COMMENT_YN, U.ID AS AUTHOR_ID, U.NAME AS AUTHOR_NAME FROM BOARD_MASTER A LEFT OUTER JOIN USERS U ON A.AUTHOR_ID = U.ID
AND A.NAME LIKE '%' || #{filter.name} || '%'
AND U.AUTHOR LIKE '%' || #{filter.author} || '%'
AND A.MODIFIED_TIME >= #{filter.startModifiedTime}
AND A.MODIFIED_TIME <= #{filter.endModifiedTime}
AND A.USE_YN = #{filter.useYn, typeHandler=BooleanToYnTypeHandler}
AND A.REPLY_YN = #{filter.replyYn, typeHandler=BooleanToYnTypeHandler}
AND A.COMMENT_YN = #{filter.commentYn, typeHandler=BooleanToYnTypeHandler}
AND A.FILE_ATTACH_YN = #{filter.fileAttachYn, typeHandler=BooleanToYnTypeHandler}
ORDER BY
A.ID
A.NAME
A.USE_YN
U.NAME
A.MODIFIED_TIME
A.REPLY_YN
A.COMMENT_YN
A.FILE_ATTACH_YN
${order.direction}
OFFSET #{pageable.offset} ROWS FETCH NEXT #{pageable.pageSize} ROWS ONLY

SELECT EXISTS(
  SELECT 1
  FROM BOARD_MASTER


      AND ID != #{id}

    AND NAME = #{name}

)
