mapper.select:
SELECT COUNT(*)
FROM USERS


        AND ID LIKE '%' || #{filter.id} || '%'


        AND NAME LIKE '%' || #{filter.name} || '%'


        AND ROLE_ID = #{filter.readRoleId}


        AND APPROVAL = #{filter.approval}

mapper.update:
UPDATE USERS
SET NAME = #{name},
    ROLE_ID = #{readRoleId}
WHERE ID = #{id}
