mapper.update:
UPDATE TEMPLATED_FLOW
SET NAME         = #{name},
    TEMPLATE_SID = #{templateSid},
    PARAMETERS   = #{parameters, typeHandler=ParameterValuesJsonTypeHandler}
WHERE ID = #{id}

mapper.select:
SELECT EXISTS(
SELECT 1
FROM TEMPLATED_FLOW


        AND ID != #{id}

    AND NAME = #{name}

)
